import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import Editor from "react-simple-code-editor";
import { highlight, languages } from "prismjs/components/prism-core";
import "prismjs/components/prism-clike";
import "prismjs/components/prism-javascript";
import axios from "axios";
import "prismjs/themes/prism-okaidia.css";
import "./editor.css";
import prettier from "prettier";


const socket = io("http://localhost:3001");

const CodeEditor = () => {
  const [code, setCode] = useState("");
  const [stream, setStream] = useState(null);
  const videoRef = useRef(null);

  useEffect(() => {
    // Set up the video stream when component mounts
    const getStream = async () => {
      try {
        const userStream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
        setStream(userStream);
        if (videoRef.current) {
          videoRef.current.srcObject = userStream;
        }
      } catch (error) {
        console.error("Error accessing media devices:", error);
      }
    };

    getStream();

    // Clean up the video stream when component unmounts
    return () => {
      if (stream) {
        stream.getTracks().forEach((track) => track.stop());
      }
    };
  }, []);

  const handleCodeChange = (newCode) => {
    console.log("New code:", newCode);
    setCode(newCode);
    socket.emit("codeChange", newCode);
  };

  useEffect(() => {
    socket.on("connect", () => {
      console.log("Connected to the server!");
    });

    socket.on("disconnect", () => {
      console.log("Disconnected from the server!");
    });

    socket.on("codeChange", (newCode) => {
      setCode(newCode);
    });
  }, []);



const socket = io("http://localhost:3001");

const CodeEditor = () => {
  const [code, setCode] = useState("");
  const [stream, setStream] = useState(null);
  const videoRef = useRef(null);

  useEffect(() => {
    // Set up the video stream when component mounts
    const getStream = async () => {
      try {
        const userStream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
        setStream(userStream);
        if (videoRef.current) {
          videoRef.current.srcObject = userStream;
        }
      } catch (error) {
        console.error("Error accessing media devices:", error);
      }
    };

    getStream();

    // Clean up the video stream when component unmounts
    return () => {
      if (stream) {
        stream.getTracks().forEach((track) => track.stop());
      }
    };
  }, []);

  const handleCodeChange = (newCode) => {
    console.log("New code:", newCode);
    setCode(newCode);
    socket.emit("codeChange", newCode);
  };

  useEffect(() => {
    socket.on("connect", () => {
      console.log("Connected to the server!");
    });

    socket.on("disconnect", () => {
      console.log("Disconnected from the server!");
    });

    socket.on("codeChange", (newCode) => {
      setCode(newCode);
    });
  }, []);

  const formatCode = () => {
    try {
      const formattedCode = prettier.format(code, {
        parser: "babel",
        plugins: [parserBabel],
      });
      setCode(formattedCode);
    } catch (error) {
      console.error("Error formatting code:", error);
    }
  };

  const handleSaveCodeVersion = () => {
    // Make a POST request to save the code version in the backend
    axios
      .post("http://localhost:3001/api/saveCodeVersion", { code })
      .then((response) => {
        console.log(response.data.message); // The server response message
        // Handle success behavior here (e.g., show success message to user)
      })
      .catch((error) => {
        console.error("Error saving code version:", error);
        // Handle error behavior here (e.g., show error message to user)
      });
  };

  return (
    <div>
      <div className="container_editor_area">
        <Editor
          value={code}
          onValueChange={(code) => handleCodeChange(code)}
          highlight={(code) => highlight(code, languages.js)}
          padding={10}
          style={{
            fontFamily: '"Fira code", "Fira Mono", monospace',
            fontSize: 12,
          }}
        />
      </div>
      <button onClick={handleSaveCodeVersion}>Save Code Version</button>
      <button onClick={formatCode}>Format Code</button>

      {stream && (
        <div>
          <h2>Your Video Stream</h2>
          <video
            style={{ width: "300px", height: "auto" }}
            ref={videoRef}
            autoPlay
            playsInline
          />
        </div>
      )}
    </div>
  );
};




