import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import Editor from "react-simple-code-editor";
import { highlight, languages } from "prismjs/components/prism-core";
import "prismjs/components/prism-clike";
import "prismjs/components/prism-javascript";
import axios from "axios";
import "prismjs/themes/prism-okaidia.css";
import "./editor.css";
import Webcam from "react-webcam";
import { useStoreState, useStoreActions } from "easy-peasy";

const socket = io("http://localhost:3001");

const CodeEditor = () => {
  const [code, setCode] = useState("");
  const webcamRef = React.useRef(null);

  // easy-peasy states and actions
  const peer = useStoreState((state) => state.peer.peer);
  const setPeerSignal = useStoreActions((actions) => actions.peer.setSignal);
  const setRemoteStream = useStoreActions((actions) => actions.peer.setRemoteStream);
  const createPeer = useStoreActions((actions) => actions.peer.createPeer);

  useEffect(() => {
    socket.on("connect", () => {
      console.log("Connected to the server!");
    });

    socket.on("disconnect", () => {
      console.log("Disconnected from the server!");
    });

    socket.on("codeChange", (newCode) => {
      setCode(newCode);
    });

    createPeer(webcamRef.current.srcObject);

    return () => {
      peer.destroy();
    };
  }, []);

  useEffect(() => {
    if (peer) {
      peer.on("signal", (data) => {
        // Send the peer signal data to the other participant
        socket.emit("signal", data);
      });

      peer.on("stream", (stream) => {
        // Receive the remote video stream
        setRemoteStream(stream);
      });
    }
  }, [peer]);

  const handleCodeChange = (newCode) => {
    console.log("New code:", newCode);
    setCode(newCode);
    socket.emit("codeChange", newCode);
  };

  const formatCode = () => {
    axios
      .post("http://localhost:3001/api/formatCode", { code })
      .then((response) => {
        console.log("Server response:", response.data);
        const formattedCode = response.data;
        console.log("Formatted code:", formattedCode);
        setCode(formattedCode);
      })
      .catch((error) => {
        console.error("Error formatting code:", error);
      });
  };

  const handleSaveCodeVersion = () => {
    axios
      .post("http://localhost:3001/api/saveCodeVersion", { code })
      .then((response) => {
        console.log(response.data.message);
        setCode(code);
      })
      .catch((error) => {
        console.error("Error saving code version:", error);
      });
  };

  return (
    <div>
      <div className="container_editor_area">
        <Editor
          value={code}
          onValueChange={(code) => handleCodeChange(code)}
          highlight={(code) => highlight(code, languages.js)}
          padding={10}
          style={{
            fontFamily: '"Fira code", "Fira Mono", monospace',
            fontSize: 12,
          }}
        />
      </div>
      <button onClick={handleSaveCodeVersion}>Save Code Version</button>
      <button onClick={formatCode}>Format Code</button>
      <div>
        <h3>Your Video</h3>
        <Webcam audio={false} ref={webcamRef} style={{ width: 200, height: 150 }} />
      </div>
      <div>
        <h3>Remote Video</h3>
        <video autoPlay style={{ width: 200, height: 150 }} />
      </div>
    </div>
  );
};

export default CodeEditor;
