import React, { useState, useEffect } from "react";
import { getFileContent } from "../../../../Server/editor";  // Replace "path/to/your/file" with the actual path to the file containing the getFileContent function.

const Files = () => {
  const [fileContent, setFileContent] = useState(null);

  const fetchFileContent = async () => {
    try {
      const filePath = 'lib/features/about_parko/screens/about_screen.dart';
      const content = await getFileContent(filePath);
      setFileContent(content);
    } catch (error) {
      console.error('Error fetching file content:', error);
    }
  };

  useEffect(() => {
    fetchFileContent();
  }, []);

  return (
    <div>
      <h1>File Content:</h1>
      <pre>{fileContent}</pre>
    </div>
  );
};

export default Files;
