import React, { useEffect } from 'react';
import './profile.css'; // Import the CSS file for styling
import NavBar from '../../components/navbar/Navbar';
import { useAuth0 } from "@auth0/auth0-react";

const UserProfile = () => {

    const { isLoading, isAuthenticated, error, myUser, loginWithRedirect, logout } =
    useAuth0();

    useEffect(() => {
     
        
    },[]);    

  const user = {
    name: 'John Doe',
    email: 'johndoe@example.com',
    image: 'https://www.gravatar.com/avatar/2c7d99fe281ecd3bcd65ab915bac6dd5?s=250',
    githubStats: {
      followers: 200,
      repos: 50,
    },
    sessionsAttended: 10,
    numOfBadges: 5,
    title: 'Merge Master',
  };

  return (
    <>
      <NavBar />
      <br />
      <div className="profile-container">
        <div className="profile-header">
          <img src={user.image} alt="Profile" className="profile-image" />
          <br />
          
          <h1 className="profile-name">{users.name}</h1>
          <p className="profile-email">{user.email}</p>
        </div>
        
        <div className="profile-details">
          <p className="sessions-attended">Sessions Attended: {user.sessionsAttended}</p>
          <p className="num-of-badges">Number of Badges: {user.numOfBadges}</p>
          <br />
          <p className="title">The {user.title}</p>
        </div>
      </div>
      <br />
      <br />
    </>
  );
};

export default UserProfile;
