import React, { useState, useEffect } from "react";
import Split from "react-split";
import './splitcontainer.css';
import axios from "axios";
import CodeEditor from "../../screens/Editor/Editor";

const Files = ({ files, onFileClick }) => {

  const fetchSubFiles = async (folderPath) => {
    try {
      const owner = 'bishalbera';
      const repo = 'Parko';
      const branch = 'master';
      const accessToken = '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';
      const url = `https://api.github.com/repos/${owner}/${repo}/contents/${folderPath}?ref=${branch}`;
      const headers = { 'Authorization': `token ${accessToken}` };
      const response = await fetch(url, { headers });

      if (!response.ok) {
        throw new Error('Error fetching sub-files');
      }

      const folderData = await response.json();
      // Filter out folders and files from the folder data
      const filteredFolderData = folderData.filter(item => item.type === 'dir' || item.type === 'file');
      setFiles(filteredFolderData);
    } catch (error) {
      console.error('Error fetching sub-files:', error);
    }
  };

  const handleFileClick = (filePath) => {
    onFileClick(filePath);
  };

  return (
    <div className="files">
      <h2>Files:</h2>
      <ul>
        {files.map((file, index) => (
          <li
            key={index}
            onClick={() => {
              if (file.type === 'file') {
                handleFileClick(file.path);
              } else if (file.type === 'dir') {
                // Fetch the sub-files when a folder is clicked
                fetchSubFiles(file.path);
              }
            }}
          >
            {file.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

const SplitContainer = ({ isSidebarCollapsed }) => {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  const fetchFiles = async () => {
    try {
      const owner = 'bishalbera';
      const repo = 'Parko';
      const branch = 'master';
      const accessToken = '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';
      const url = `https://api.github.com/repos/${owner}/${repo}/contents?ref=${branch}`;
      const headers = { 'Authorization': `token ${accessToken}` };
      const response = await fetch(url, { headers });

      if (!response.ok) {
        throw new Error('Error fetching file list');
      }

      const filesData = await response.json();
      // Filter out folders from the files data
      const filteredFiles = filesData.filter(file => file.type === 'file');
      setFiles(filteredFiles);
    } catch (error) {
      console.error('Error fetching file list:', error);
    }
  };

  

  

  const handleFileClick = async (filePath) => {
    try {
      const contentResponse = await axios.get(filePath);
      setSelectedFile(contentResponse.data);
    } catch (error) {
      console.error('Error fetching file content:', error);
      setSelectedFile(null); // Set selectedFile to null in case of error
    }
  };

  useEffect(() => {
    fetchFiles();
  }, []);

  const splitSizes = isSidebarCollapsed ? [0, 100] : [15, 85];

  return (
    <div className="splitContainer">
      <Split
        className="container"
        sizes={splitSizes}
        minSize={0}
        expandToMin={false}
        gutterSize={5}
        gutterAlign="center"
        snapOffset={30}
        dragInterval={1}
        direction="horizontal"
        cursor="col-resize"
      >
        <Files files={files} onFileClick={handleFileClick} />
        {selectedFile !== null ? (
          <CodeEditor content={selectedFile} />
        ) : (
          <div className="codeEditor">Select a file to view its content</div>
        )}
      </Split>
    </div>
  );
};

export default SplitContainer;
