    }
  
    const splitSizes = isSidebarCollapsed ? [0,100] : [15,85]
  
    function deleteFileFromFiles(filename) {
      setFiles((current) => {
        delete current[filename];
        return current;
      });
    }
  
    return (
      <>
         <Split
        className="container"
        sizes={splitSizes}
