        expandToMin={false}
        gutterSize={5}
        gutterAlign="center"
        snapOffset={30}
        dragInterval={1}
        direction="horizontal"
        cursor="col-resize"
      >
        


        <div className="editor">
          {
            <CodeEditor/>
          }
        </div>
      </Split>
      </>
    );
  };
  
  export default SplitContainer;
