import React, { useState, useEffect } from "react";
import Split from "react-split";
import './splitcontainer.css';

const Files = ({ files, onFileClick }) => {
  return (
    <div className="files">
      <h2>Files:</h2>
      <ul>
        {files.map((file, index) => (
          <li key={index} onClick={() => onFileClick(file.path)}>
            {file.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

const CodeEditor = ({ content }) => {
  return (
    <div className="codeEditor">
      <h2>File Content:</h2>
      <pre>
        <code>{content}</code>
      </pre>
    </div>
  );
};

const SplitContainer = ({ isSidebarCollapsed }) => {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  const getFileContent = async (filePath) => {
    // GitHub API call to fetch file content
    // Implement your logic to fetch content similar to your previous code
    // Make sure to use a server-side implementation to keep the access token secure
  };

  const fetchFiles = async () => {
    try {
      const owner = 'bishalbera';
      const repo = 'Parko';
      const branch = 'master';
      const accessToken = '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';
      const url = `https://api.github.com/repos/${owner}/${repo}/contents?ref=${branch}`;
      const headers = { 'Authorization': `token ${accessToken}` };
      const response = await fetch(url, { headers });

      if (!response.ok) {
        throw new Error('Error fetching file list');
      }

      const filesData = await response.json();
      setFiles(filesData);
    } catch (error) {
      console.error('Error fetching file list:', error);
    }
  };

  const handleFileClick = async (filePath) => {
    try {
      const content = await getFileContent(filePath);
      setSelectedFile(content);
    } catch (error) {
      console.error('Error fetching file content:', error);
      setSelectedFile(null); // Set selectedFile to null in case of error
    }
  };

  useEffect(() => {
    fetchFiles();
  }, []);

  const splitSizes = isSidebarCollapsed ? [0, 100] : [15, 85];

  return (
    <div className="splitContainer">
      <Split
        className="container"
        sizes={splitSizes}
        minSize={0}
        expandToMin={false}
        gutterSize={5}
        gutterAlign="center"
        snapOffset={30}
        dragInterval={1}
        direction="horizontal"
        cursor="col-resize"
      >
        <Files files={files} onFileClick={handleFileClick} />
        {selectedFile !== null ? (
          <CodeEditor content={selectedFile} />
        ) : (
          <div className="codeEditor">Select a file to view its content</div>
        )}
      </Split>
    </div>
  );
};

export default SplitContainer;
