import React, { useState, useEffect } from "react";
import Split from "react-split";
import './splitcontainer.css';
import axios from 'axios';

const Files = ({ files, onFileClick }) => {
  if (!files || !Array.isArray(files)) {
    return <div className="files">No files found</div>;
  }

  const renderFilesAndFolders = (files) => {
    return files.map((file, index) => {
      if (file.type === 'dir') {
        return (
          <li key={index}>
            <strong>{file.name}</strong>
            <ul>{renderFilesAndFolders(file.children)}</ul>
          </li>
        );
      } else {
        return (
          <li key={index} onClick={() => onFileClick(file.path)}>
            {file.name}
          </li>
        );
      }
    });
  };

  return (
    <div className="files">
      <h2>Files:</h2>
      <ul>{renderFilesAndFolders(files)}</ul>
    </div>
  );
};

const CodeEditor = ({ content }) => {
  return (
    <div className="codeEditor">
      <h2>File Content:</h2>
      <pre>
        <code>{content}</code>
      </pre>
    </div>
  );
};

const SplitContainer = ({ isSidebarCollapsed }) => {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileContent, setFileContent] = useState('');

  const getFileContent = async (filePath) => {
    const owner = 'bishalbera';
    const repo = 'Parko';
    const branch = 'master';
    const accessToken = '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';

    const url = `https://api.github.com/repos/${owner}/${repo}/contents/${filePath}?ref=${branch}`;
    const headers = {
      'Authorization': `token ${accessToken}`,
      'Accept': 'application/vnd.github.v3.raw', // Request raw content directly
    };

    try {
      const response = await axios.get(url, { headers });

      // Set the fileContent state after getting the content
      setFileContent(response.data);
      return response.data;
    } catch (error) {
      console.error('Error fetching file content:', error);
      throw new Error('Error fetching file content');
    }
  };

  const fetchFiles = async (path = '') => {
    try {
      const owner = 'bishalbera';
      const repo = 'Parko';
      const branch = 'master';
      const accessToken = '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';
      const url = `https://api.github.com/repos/${owner}/${repo}/contents${path}?ref=${branch}`;
      const headers = { 'Authorization': `token ${accessToken}` };
      const response = await fetch(url, { headers });

      if (!response.ok) {
        throw new Error('Error fetching file list');
      }

      const filesData = await response.json();
      const filesWithChildren = filesData.map((file) => {
        if (file.type === 'dir') {
          return { ...file, children: [] };
        } else {
          return file;
        }
      });

      // Recursively fetch and add children to folders
      for (const file of filesWithChildren) {
        if (file.type === 'dir') {
          file.children = await fetchFiles(file.path);
        }
      }

      return filesWithChildren;
    } catch (error) {
      console.error('Error fetching file list:', error);
      return [];
    }
  };

  const handleFileClick = async (filePath) => {
    try {
      const content = await getFileContent(filePath);
      setSelectedFile(content);
    } catch (error) {
      console.error('Error fetching file content:', error);
      setSelectedFile(null); // Set selectedFile to null in case of error
    }
  };

  useEffect(() => {
    fetchFiles();
  }, []);

  const splitSizes = isSidebarCollapsed ? [0, 100] : [15, 85];

  return (
    <div className="splitContainer">
      <Split
        className="container"
        sizes={splitSizes}
        minSize={0}
        expandToMin={false}
        gutterSize={5}
        gutterAlign="center"
        snapOffset={30}
        dragInterval={1}
        direction="horizontal"
        cursor="col-resize"
      >
        <Files files={files} onFileClick={handleFileClick} />
        {selectedFile !== null ? (
          <CodeEditor content={fileContent} />
        ) : (
          <div className="codeEditor">Select a file to view its content</div>
        )}
      </Split>
    </div>
  );
};

export default SplitContainer;
