import React, { useState, useEffect } from "react";
import Split from "react-split";
import './splitcontainer.css';
import CodeEditor from "../../screens/Editor/Editor";
import Files from "./Files";


const SplitContainer = ({ param, isSidebarCollapsed }) => {
  const [fileContent, setFileContent] = useState("");
  
    // githubApi.js

const getFileContent = async (filePath) => {
    const owner = 'bishalbera';
    const repo = 'Parko';
    const branch = 'master';
    const accessToken = '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';
  
    const url = `https://api.github.com/repos/${owner}/${repo}/contents/${filePath}?ref=${branch}`;
    const headers = { 'Authorization': `token ${accessToken}` };
  
    const response = await fetch(url, { headers });
  
    if (!response.ok) {
      throw new Error('Error fetching file content');
    }
  
    const fileData = await response.json();
  
    if (fileData.encoding === 'base64' && fileData.type === 'file') {
      return atob(fileData.content);
    } else {
      throw new Error('Invalid file format');
    }
  };
  
  
  

  const getFiles = async () => {
    try {
      const filePath = 'lib/features/about_parko/screens/about_screen.dart';
      const content = await getFileContent(filePath);
      setFileContent(content);
    } catch (error) {
      console.error('Error fetching file content:', error);
    }
  };

  useEffect(() => {
    getFiles();
  }, []);

  const splitSizes = isSidebarCollapsed ? [0, 100] : [15, 85];

  

  return (
    <>
  <div className="splitContainer"></div>

      <Split
        className="container"
        sizes={splitSizes}
        minSize={0}
        expandToMin={false}
        gutterSize={5}
        gutterAlign="center"
        snapOffset={30}
        dragInterval={1}
        direction="horizontal"
        cursor="col-resize"
      >
       

        <div className="files">
            <Files />
        </div>

       
      </Split>
    </>
  );
};

export default SplitContainer;
